import "core-js/modules/es.array.push.js";
import request from "@/utils/request";
export default {
  name: "Edit",
  data() {
    const checkEmail = (rule, value, callback) => {
      if (!value) {
        callback(new Error('Please enter the email address'));
      }
      if (!/^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+(.[a-zA-Z0-9_-])+/.test(value)) {
        callback(new Error('Illegal email address'));
      }
      callback();
    };
    const checkPhone = (rule, value, callback) => {
      if (!value) {
        callback(new Error('Please enter the phone number'));
      }
      if (!/^[1,2,3,4,5,6,7,8,9][0-9]{9}$/.test(value)) {
        callback(new Error('Illegal phone number'));
      }
      callback();
    };
    return {
      form: {},
      rules: {
        minit: [{
          required: false
        }],
        // cannot be empty
        fname: [{
          required: true,
          message: 'Please enter the first name',
          trigger: 'blur'
        }],
        lname: [{
          required: true,
          message: 'Please enter the last name',
          trigger: 'blur'
        }],
        username: [{
          required: true,
          message: 'Please enter the username',
          trigger: 'blur'
        }],
        province: [{
          required: true,
          message: 'Please enter the province/state',
          trigger: 'blur'
        }],
        city: [{
          required: true,
          message: 'Please enter the city',
          trigger: 'blur'
        }],
        street: [{
          required: true,
          message: 'Please enter the street address',
          trigger: 'blur'
        }],
        // more restrictions
        email: [{
          required: true,
          validator: checkEmail,
          trigger: 'blur'
        }],
        phone: [{
          required: true,
          validator: checkPhone,
          trigger: 'blur'
        }]
      }
    };
  },
  created() {
    const email = this.$route.query.email;
    request.get('/admin/' + email).then(res => {
      this.form = res.data;
    });
  },
  methods: {
    save() {
      this.$refs['ruleForm'].validate(valid => {
        if (valid) {
          request.put('admin/update', this.form).then(res => {
            if (res.code === '200') {
              this.$notify.success('Updated');
              this.$router.push("/adminList");
            } else {
              this.$notify.error(res.msg);
            }
          });
        }
      });
    }
  }
};
